{"metadata":{"kernelspec":{"language":"python","display_name":"Python 3","name":"python3"},"language_info":{"name":"python","version":"3.11.13","mimetype":"text/x-python","codemirror_mode":{"name":"ipython","version":3},"pygments_lexer":"ipython3","nbconvert_exporter":"python","file_extension":".py"},"kaggle":{"accelerator":"none","dataSources":[],"dockerImageVersionId":31153,"isInternetEnabled":false,"language":"python","sourceType":"notebook","isGpuEnabled":false}},"nbformat_minor":4,"nbformat":4,"cells":[{"cell_type":"code","source":"import streamlit as st\nimport numpy as np\nimport pandas as pd\n\n# --- Page setup ---\nst.set_page_config(page_title=\"Law of Large Numbers\", layout=\"centered\")\nst.title(\"Law of Large Numbers â€“ Bernoulli Simulation\")\n\nst.markdown(\n    \"\"\"\n    This app simulates Bernoulli trials for different sample sizes  \n    and shows how the empirical frequency converges to the probability *p*.\n    \"\"\"\n)\n\n# --- User input ---\np = st.slider(\"Choose probability p\", min_value=0.0, max_value=1.0, value=0.5, step=0.01)\nseed = st.number_input(\"Random seed (optional)\", value=42, step=1)\ninclude_big_n = st.checkbox(\"Include n = 10,000,000 (may take a while)\", value=False)\n\n# Sample sizes\nn_values = [10, 50, 100, 1000] + ([10_000_000] if include_big_n else [])\n\nrun = st.button(\"Run simulation\")\n\n# --- Computation ---\nif run:\n    rng = np.random.default_rng(int(seed))\n\n    rows = []\n    for n in n_values:\n        successes = rng.binomial(n=n, p=p)\n        result = successes / n\n        abs_diff = abs(result - p)\n        rows.append({\"n\": n, \"result\": result, \"|result - p|\": abs_diff})\n\n    df = pd.DataFrame(rows)\n\n    # --- Results ---\n    st.subheader(\"Results\")\n    st.dataframe(\n        df.style.format({\"result\": \"{:.4f}\", \"|result - p|\": \"{:.4f}\"}),\n        use_container_width=True,\n    )\n\n    # --- Visualization ---\n    st.subheader(\"Visualization\")\n    st.line_chart(df.set_index(\"n\")[[\"result\"]], x_label=\"n\", y_label=\"result\")\n    st.line_chart(df.set_index(\"n\")[[\"|result - p|\"]], x_label=\"n\", y_label=\"|result - p|\")\n\n    st.caption(\n        \"The theoretical value is p. As n increases, the empirical frequency \"\n        \"tends to get closer to p, illustrating the law of large numbers.\"\n    )\n\nelse:\n    st.info(\"Set parameters and click **Run simulation**.\")","metadata":{"trusted":true,"execution":{"iopub.status.busy":"2025-10-27T11:13:57.257812Z","iopub.execute_input":"2025-10-27T11:13:57.258129Z"}},"outputs":[{"name":"stdout","text":"\u001b[33mWARNING: Retrying (Retry(total=4, connect=None, read=None, redirect=None, status=None)) after connection broken by 'NewConnectionError('<pip._vendor.urllib3.connection.HTTPSConnection object at 0x7a6a9518f9d0>: Failed to establish a new connection: [Errno -3] Temporary failure in name resolution')': /simple/streamlit/\u001b[0m\u001b[33m\n\u001b[0m","output_type":"stream"}],"execution_count":null}]}